import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { StyleSheet, Text, TouchableOpacity, View, Dimensions, Pressable, TextInput, ActivityIndicator, Modal, SafeAreaView, ScrollView } from 'react-native';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faSolid, faSackDollar, faCircleArrowLeft, faX, faUserShield, faStar, faPenToSquare, faCopy, faMinus, faMessage, faTrash, faClock, faCheck, faGift, faFlagCheckered } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios';
// import data from './wordlist.json';


const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;


export const Grid = () => {

    const [words, setWords] = useState([])
    let wordArray = []
    // let wordList = [];
    let CompArray_0 = [];
    let CompArray_1 = [];
    let indexArray_0 = [];
    let indexArray_1 = [];
    // const [storeCompArry, setStoreCompArray] = useState([]);
    let tempGridArray_0 = ['', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '']
    let localSplitWord_1 = [];
    let localSplitWord_2 = [];
