    const [word2, setWord2] = useState('');
    let buttonArray = [];
    // let upper = [];
    let layer_0 = []
    let layer_1 = []
    let layer_2 = []
    let layer_3 = []
    // let keyContainer_0 = ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'];
    // let keyContainer_1 = ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L',];
    // let keyContainer_2 = ['Z', 'X', 'C', 'V', 'B', 'N', 'M',];
