export const Reveal = () => {
    const [iVar, setIVar] = useState('');
    const [highlight, setHighlight] = useState(false);
    let buttonArray = [];


    for (let i = 0; i < 25; i++) {
        buttonArray[i] =
            <>
                {highlight && i == iVar ?
                    <TouchableOpacity
                        onPress={() => { setHighlight(false) }}
                        style={{ height: windowWidth / 6, width: windowWidth / 6, margin: 2, borderWidth: 2, borderColor: '#80ffdb' }}
                        key={i}
                    >
                        {/* <Text style={{ color: '#f20089', alignSelf: 'center', marginTop: 30 }}>{i}</Text> */}
                    </TouchableOpacity>
                    :
                    <TouchableOpacity
                        onPress={() => { setHighlight(true); setIVar(i) }}
                        style={{ height: windowWidth / 6, width: windowWidth / 6, margin: 2, backgroundColor: 'rgba(255, 255, 255, 1)' }}
                        key={i}
                    >
                        {/* <Text style={{ color: '#f20089', alignSelf: 'center', marginTop: 30 }}>{i}</Text> */}
                    </TouchableOpacity>
                }

            </>
    }
    return buttonArray;

}

